mapper.select:
SELECT rc.car_code, ct.car_type_name, ct.car_type_code, rc.car_number, cc1.common_name AS car_status, rc.car_status AS car_status_code, b.branch_name, b.branch_code, cc2.common_name AS car_type_category, cc3.common_name AS origin_type, cc4.common_name AS seating_capacity, cc5.common_name AS fuel_type, cc6.common_name AS car_manufacturer, rc.model_year FROM rental_cars rc JOIN branchs b ON rc.branch_code = b.branch_code JOIN car_types ct ON rc.car_type_code = ct.car_type_code JOIN common_codes cc1 ON cc1.common_code = rc.car_status AND cc1.common_kind_code = 'car_status' JOIN common_codes cc2 ON cc2.common_code = ct.car_type_category AND cc2.common_kind_code = 'car_type_category' JOIN common_codes cc3 ON cc3.common_code = ct.origin_type AND cc3.common_kind_code = 'origin_type' JOIN common_codes cc4 ON cc4.common_code = ct.seating_capacity AND cc4.common_kind_code = 'seating_capacity' JOIN common_codes cc5 ON cc5.common_code = ct.fuel_type AND cc5.common_kind_code = 'fuel_type' JOIN common_codes cc6 ON cc6.common_code = ct.car_manufacturer AND cc6.common_kind_code = 'car_manufacturer' WHERE 1=1
AND rc.car_number LIKE CONCAT('%', #{carNumber}, '%')
AND rc.car_status = #{carStatus}
AND b.branch_name = #{branchName}
AND ct.car_type_name = #{carTypeName}
AND cc2.common_name = #{carTypeCategory}
AND cc3.common_name = #{originType}
AND cc4.common_name = #{seatingCapacity}
AND cc5.common_name = #{fuelType}
AND cc6.common_name = #{carManufacturer}
AND rc.model_year = #{modelYear}
ORDER BY car_code LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*)
FROM rental_cars rc


    JOIN branchs b ON rc.branch_code = b.branch_code
    JOIN car_types ct ON rc.car_type_code = ct.car_type_code
    JOIN common_codes cc1 ON cc1.common_code = ct.car_type_category AND cc1.common_kind_code = 'car_type_category'
    JOIN common_codes cc2 ON cc2.common_code = ct.origin_type AND cc2.common_kind_code = 'origin_type'
    JOIN common_codes cc3 ON cc3.common_code = ct.seating_capacity AND cc3.common_kind_code = 'seating_capacity'
    JOIN common_codes cc4 ON cc4.common_code = ct.fuel_type AND cc4.common_kind_code = 'fuel_type'
    JOIN common_codes cc5 ON cc5.common_code = ct.car_manufacturer AND cc5.common_kind_code = 'car_manufacturer'

WHERE 1=1

    AND rc.car_number LIKE CONCAT('%', #{carNumber}, '%')


    AND rc.car_status = #{carStatus}


    AND b.branch_name = #{branchName}


    AND ct.car_type_name = #{carTypeName}


    AND cc2.common_name = #{carTypeCategory}


    AND cc3.common_name = #{originType}


    AND cc4.common_name = #{seatingCapacity}


    AND cc5.common_name = #{fuelType}


    AND cc6.common_name = #{carManufacturer}


    AND rc.model_year = #{modelYear}
