mapper.select:
SELECT r.reservation_code AS reservation_code, u.user_name AS user_name, rc.car_number AS car_number, cc_car_status.common_name AS car_status, ct.car_type_name AS car_type_name, rl.branch_name AS rental_location_name, r.rental_date AS rental_date, r.return_date AS return_date FROM reservations r JOIN users u ON r.user_code = u.user_code JOIN rental_cars rc ON r.car_code = rc.car_code JOIN car_types ct ON rc.car_type_code = ct.car_type_code JOIN branchs rl ON r.rental_location = rl.branch_code JOIN common_codes cc_car_status ON rc.car_status = cc_car_status.common_code AND cc_car_status.common_kind_code = 'car_status' WHERE 1=1
AND rental_location_name = #{rentalLocationName}
AND rental_date = #{rentalDate}
AND user_name = #{userName}
ORDER BY rental_date DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM reservations r
JOIN users u ON r.user_code = u.user_code
JOIN branchs rl ON r.rental_location = rl.branch_code
WHERE 1=1

    AND rl.branch_name = #{rentalLocationName}


    AND r.rental_date = #{rentalDate}


    AND u.user_name = #{userName}

mapper.update:
UPDATE rental_cars
SET car_status = #{carStatus}
WHERE car_code = (
SELECT car_code
FROM reservations
WHERE reservation_code = #{reservationCode}
);

UPDATE reservations
SET reservation_status = #{reservationStatus}
WHERE reservation_code = #{reservationCode}

UPDATE reservations
SET
reservation_status = #{reservationStatus},
payment_status = #{paymentStatus}
WHERE
reservation_code = #{reservationCode};

UPDATE rental_cars
SET
car_status = #{carStatus}
WHERE
car_code = (
SELECT car_code
FROM reservations
WHERE reservation_code = #{reservationCode}
);

mapper.select:
SELECT r.reservation_code AS reservationCode, r.reservation_date AS reservationDate, r.rental_date AS rentalDate, rl.branch_name AS rentalLocationName, r.return_date AS returnDate, rt.branch_name AS returnLocationName, ct.car_type_name AS carTypeName, cc.common_name AS reservationStatus FROM reservations r JOIN users u ON r.user_code = u.user_code JOIN branchs rl ON r.rental_location = rl.branch_code JOIN branchs rt ON r.return_location = rt.branch_code JOIN rental_cars rc ON r.car_code = rc.car_code JOIN car_types ct ON rc.car_type_code = ct.car_type_code JOIN common_codes cc ON r.reservation_status = cc.common_code AND cc.common_kind_code = 'reservation_status' WHERE u.user_code = #{userCode}
AND rl.branch_name = #{rentalLocationName}
AND r.reservation_date = #{reservationDate}
ORDER BY r.reservation_date DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM reservations
WHERE user_code = #{userCode}

    AND rental_location = #{rentalLocationName}


    AND reservation_date = #{reservationDate}

mapper.update:
UPDATE rental_cars
SET
car_status = #{carStatus}
WHERE
car_code = (
SELECT car_code
FROM reservations
WHERE user_code = #{userCode}
AND reservation_code = #{reservationCode}
);

UPDATE reservations
SET
reservation_status = #{reservationStatus},
payment_status = #{paymentStatus}
WHERE
user_code = #{userCode}
AND reservation_code = #{reservationCode};
